var date = new Date();
export default {
  data: function data() {
    var _this = this;

    return {
      ruleValidate: {
        title: [{
          required: true,
          message: '不能为空',
          trigger: 'blur'
        }],
        main: [{
          required: true,
          message: '不能为空',
          trigger: 'blur'
        }]
      },
      ruleValidate2: {
        title: [{
          required: true,
          message: '不能为空',
          trigger: 'blur'
        }],
        main: [{
          required: true,
          message: '不能为空',
          trigger: 'blur'
        }]
      },
      type: [{
        value: '2',
        label: '指定用户'
      }, {
        value: '1',
        label: '所有用户'
      }],
      date: '',
      time: '',
      adddate: '',
      addtime: '',
      formAdd: {
        title: '',
        main: '',
        jwt: localStorage.getItem('jwt')
      },
      formItem: {
        Id: '',
        title: '',
        main: '',
        jwt: localStorage.getItem('jwt')
      },
      formImg: {
        Id: "",
        img: null,
        jwt: localStorage.getItem('jwt')
      },
      modal1: false,
      modal2: false,
      modal3: false,
      loading: true,
      loading1: true,
      modal3type: true,
      //    分页
      ajaxHistoryData: [],
      // 初始化信息总条数
      dataCount: 0,
      // 每页显示多少条
      pageSize: 10,
      page: 1,
      columns1: [{
        title: '时间',
        key: 'time'
      }, {
        title: '标题',
        key: 'title'
      }, {
        title: '内容',
        key: 'main'
      }, {
        title: '状态',
        key: 'type'
      }, {
        title: '操作',
        key: 'action',
        align: 'center',
        render: function render(h, params) {
          return h('div', [h('Button', {
            props: {
              type: 'info',
              size: 'small'
            },
            style: {
              marginRight: '5px',
              marginBottom: '3px'
            },
            on: {
              click: function click() {
                _this.xiugai(params.row);
              }
            }
          }, '修改'), h('Button', {
            props: {
              type: 'info',
              size: 'small'
            },
            style: {
              marginRight: '5px',
              marginBottom: '3px'
            },
            attrs: {
              style: 'background-color:#d6514d;border:1px solid #d6514d'
            },
            on: {
              click: function click() {
                _this.delete(params.row);
              }
            }
          }, '删除')]);
        }
      }],
      data1: []
    };
  },
  created: function created() {
    console.log(date, '当前时间');
    this.tableShow();
  },
  methods: {
    //用户信息展示
    tableShow: function tableShow() {
      var _this2 = this;

      this.$axios({
        method: 'post',
        url: 'admin/mail/show',
        data: this.$qs.stringify({
          page: this.page,
          jwt: localStorage.getItem('jwt')
        })
      }).then(function (res) {
        // console.log(res.data.table,'用户信息')
        _this2.ajaxHistoryData = res.data.table;
        _this2.dataCount = res.data.count; // 初始化显示，小于每页显示条数，全显，大于每页显示条数，取前每页条数显示

        if (res.data.count < _this2.pageSize) {
          // this.historyData = this.ajaxHistoryData;
          _this2.data1 = res.data.table;
        } else {
          _this2.data1 = _this2.ajaxHistoryData.slice(0, _this2.pageSize);
        }
      }).catch(function (res) {
        _this2.$Message.error('请求超时,请稍后再试...');
      });
    },
    //新增
    add: function add() {
      this.modal3 = true;
    },
    ok3: function ok3(formAdd) {
      var _this3 = this;

      this.$refs[formAdd].validate(function (valid) {
        if (valid) {
          _this3.$axios({
            method: 'post',
            url: 'admin/mail/insert',
            data: _this3.$qs.stringify({
              title: _this3.formAdd.title,
              main: _this3.formAdd.main,
              jwt: localStorage.getItem('jwt')
            })
          }).then(function (res) {
            _this3.tableShow();

            _this3.modal3 = false;
            _this3.formAdd = {};
          }).catch(function (res) {
            _this3.$Message.error('请求超时,请稍后再试...');
          });

          callback();
        } else {
          // console.log('新增失败')
          _this3.loading = false;

          _this3.$nextTick(function () {
            _this3.loading = true;
          });

          _this3.$Message.error('新增失败,请正确填写信息!');
        }
      });
      this.tableShow();
      this.formAdd = {};
    },
    cancel3: function cancel3() {
      this.tableShow();
      this.formAdd = {}; // this.$Message.info('Clicked cancel');
    },
    //用户修改
    xiugai: function xiugai(row) {
      // console.log(row)
      this.modal1 = true;
      this.formItem = row;
      this.formItem.Id = row.Id;
    },
    ok: function ok(formItem) {
      var _this4 = this;

      this.$refs[formItem].validate(function (valid) {
        if (valid) {
          _this4.$axios({
            method: 'post',
            url: 'admin/mail/upd',
            data: _this4.$qs.stringify({
              Id: _this4.formItem.Id,
              title: _this4.formItem.title,
              main: _this4.formItem.main,
              jwt: localStorage.getItem('jwt')
            })
          }).then(function (res) {
            // console.log(res,'修改')
            _this4.modal3 = false;
            _this4.modal1 = false;

            _this4.tableShow();
          }).catch(function (res) {
            _this4.$Message.error('请求超时,请稍后再试...');
          });

          callback();
        } else {
          _this4.loading1 = false;

          _this4.$nextTick(function () {
            _this4.loading1 = true;
          });

          _this4.$Message.error('修改失败,请正确填写信息!');
        }
      });
    },
    cancel: function cancel() {
      this.modal3 = false; // this.$Message.info('Clicked cancel');
    },
    //删除
    delete: function _delete(row) {
      var _this5 = this;

      // console.log(row)
      this.$Modal.confirm({
        title: '删除',
        content: '确认删除该条信息吗？',
        onOk: function onOk() {
          _this5.$axios({
            method: 'post',
            url: 'admin/Mail/delete',
            data: _this5.$qs.stringify({
              Id: row.Id,
              jwt: localStorage.getItem('jwt')
            })
          }).then(function (res) {
            _this5.tableShow();
          }).catch(function (res) {
            _this5.$Message.error('请求超时,请稍后再试...');
          });
        },
        onCancel: function onCancel() {
          _this5.$Message.info('取消');
        }
      });
    },
    //分页
    changepage: function changepage(index) {
      this.page = index;
      this.tableShow();

      var _start = (index - 1) * this.pageSize;

      var _end = index * this.pageSize;

      this.data1 = this.ajaxHistoryData.slice(_start, _end);
    }
  }
};
