export default {
  data () {
    const validatePhone = (rule, value, callback) => {
      if (!value) {
        return callback(new Error('手机号不能为空'));
      } else if (!/^1[34578]\d{9}$/.test(value)) {
        callback('手机号格式不正确');
      } else {
        callback();
      }
    };
    const validatePass = (rule, value, callback) => {
      // 此时便能打印value值查看
      console.log(value,'打印值')
    };
    return {
      ruleValidate: {
        name: [
          { required: true, message: '不能为空', trigger: 'blur' }
        ],
        phone: [
          // { required: true, message: '不能为空', trigger: 'blur' },
          {type: 'number',required:true,validator:validatePhone,message:'请输入正确的手机号',trigger:'change'}
        ],
        grade: [
          { required: true, message: '不能为空', trigger: 'blur' }
        ],
        arrangement:[
          { required: true, message: '不能为空', trigger: 'blur' }
        ],
        total: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        quota: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        provinceid: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        cityid: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        areaid: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ]
      },
      ruleValidate2: {
        name: [
          { required: true, message: '不能为空', trigger: 'blur' }
        ],
        phone: [
          // { required: true, message: '不能为空', trigger: 'blur' },
          {type: 'number',required:true,validator:validatePhone,message:'请输入正确的手机号',trigger:'change'}
        ],
        grade: [
          { required: true, message: '不能为空', trigger: 'blur' }
        ],
        arrangement:[
          { required: true, message: '不能为空', trigger: 'blur' }
        ],
        total: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        quota: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        pass: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        provinceid: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        cityid: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ],
        areaid: [
          { required: true, message: '不能为空', trigger: 'blur' },
        ]
      },
      searchName:'',
      searchNum:'',
      searchPhone:'',
      cityList:[],
      cityList1:[],
      cityList2:[],
      cityListId:'',
      cityListId1:'',
      cityListId2:'',
      formAdd:{
        name:'',
        phone:'',
        grade:'',
        arrangement:'',
        total:'',
        quota:'',
        cityid:'',
        provinceid:'',
        areaid:'',
        jwt:localStorage.getItem('jwt')
      },
      formItem:{
        Id:'',
        name:'',
        phone:'',
        grade:'',
        arrangement:'',
        total:'',
        quota:'',
        city:'',
        pass:'',
        provinceid:'',
        cityid:'',
        areaid:'',
        jwt:localStorage.getItem('jwt')
      },
      formImg:{
        Id:"",
        img:null,
        jwt:localStorage.getItem('jwt')
      },
      modal1: false,
      modal2: false,
      modal3:false,
      loading: true,
      loading1: true,
      //    分页
      ajaxHistoryData: [],
      // 初始化信息总条数
      dataCount: 0,
      // 每页显示多少条
      pageSize: 5,
      columns1: [
        {
          title: '姓名',
          key: 'name',
          width: 120
        },
        {
          title: '照片',
          key: 'img',
          render: (h, params) => {
            // console.log(params.row)
            return h('img', {
              style: {
                width: '100px',
                height: '100px',
                margin: 'auto'
              },
              attrs: {
                src: params.row.img, style: 'width: 100px;height: 100px;margin: auto'
              }
              // on: {
              //     click: () => {
              //         this.hangImg(1, params.row);
              //     }
              // }
            });
          }
        },
        {
          title: '手机号',
          key: 'phone',
          width: 120
        },
        {
          title: '工号',
          key: 'number'
        },
        {
          title: '等级',
          key: 'grade'
        },
        {
          title: '技术指数',
          key: 'arrangement'
        },
        {
          title: '总额',
          key: 'total'
        },
        {
          title: '领用额度',
          key: 'quota'
        },
        {
          title: '上门城市',
          key: 'city'
        },
        {
          title: '密码',
          key: 'pass'
        },
        {
          title: '创建时间',
          key: 'time',
          width: 150
        },
        {
          title: '操作',
          key: 'action',
          align: 'center',
          render: (h, params) => {
              return h('div', [
                h('Button', {
                  props: {
                    type: 'info',
                    size: 'small'
                  },
                  style: {
                    marginRight: '5px',
                    marginBottom: '5px'
                  },
                  on: {
                    click: () => {
                      this.xiugai(params.row);
                    }
                  }
                }, '修改'),
                h('Button', {
                  props: {
                    type: 'info',
                    size: 'small'
                  },
                  style: {
                    marginRight: '5px',
                    marginBottom: '5px'
                  },
                  on: {
                    click: () => {
                      this.delete(params.row);
                    }
                  }
                }, '删除'),
                h('Button', {
                  props: {
                    type: 'info',
                    size: 'small'
                  },
                  style: {
                    marginRight: '5px'
                  },
                  on: {
                    click: () => {
                      this.xiugaiimg(params.row);
                    }
                  }
                }, '图片修改上传'),
              ]);
          }
        }
      ],
      data1: []
    }
  },
  created() {
    // console.log(localStorage.getItem('jwt'))
    this.tableShow()
  },
  methods: {
    //用户信息展示
    tableShow(){
      this.$axios({
        method: 'post',
        url: 'admin/user/show',
        data: this.$qs.stringify({
          name:this.searchName,
          phone:this.searchPhone,
          number:this.searchNum,
          jwt: localStorage.getItem('jwt')
        })
      }).then(res => {
        // console.log(res.data.table,'用户信息')
        this.ajaxHistoryData = res.data.table;
        this.dataCount = res.data.table.length;
        // 初始化显示，小于每页显示条数，全显，大于每页显示条数，取前每页条数显示
        if (res.data.table.length < this.pageSize) {
          // this.historyData = this.ajaxHistoryData;
          this.data1 = res.data.table
        } else {
          this.data1 = this.ajaxHistoryData.slice(0, this.pageSize);
        }
      }).catch(res => {
        this.$Message.error('请求超时,请稍后再试...');
      });
    },
    //搜索
    handSeek(){
      this.tableShow()
    },
    //新增
    add(){
      this.modal3=true
      this.$axios({
        url: 'admin/user/adress',
        method: 'get',
        // data: $qs.stringify({
        //   Id: isDelete
        // })
      }).then(res => {
        // console.log(res,'12345')
        this.cityList = res.data.table
      }).catch(res => {
        this.$Message.error('请求超时,请稍后再试...')
      });
    },
    //省联动
    chioce(item){
      this.cityListId=item.id
      this.$axios({
        method: 'post',
        url: 'admin/user/getRegion',
        data: this.$qs.stringify({
          id: item.id,
          type: item.type
          // jwt:localStorage.getItem('jwt')
        })
      }).then(res => {
        // console.log(res,'市三级联动')
        this.cityList1=res.data
      }).catch(res => {
        this.$Message.error('请求超时,请稍后再试...');
      });
    },
    //市联动
    chioce1(item){
      // console.log(JSON.stringify(item),'12345')
      this.cityListId1=item.id
      this.$axios({
        method: 'post',
        url: 'admin/user/getRegion',
        data: this.$qs.stringify({
          id: item.id,
          type: item.type
          // jwt:localStorage.getItem('jwt')
        })
      }).then(res => {
        // console.log(res,'市三级联动')
        this.cityList2=res.data
      }).catch(res => {
        this.$Message.error('请求超时,请稍后再试...');
      });
    },
    //区联动
    chioce2(item){
      this.cityListId2=item.id
    },
    ok3 (formAdd) {
      // console.log(this.$nextTick,'验证')
      this.$refs[formAdd].validate((valid) => {
        if (valid) {
          this.$axios({
                method: 'post',
                url: 'admin/user/insert',
                data: this.$qs.stringify({
                  name: this.formAdd.name,
                  phone: this.formAdd.phone,
                  grade: this.formAdd.grade,
                  arrangement: this.formAdd.arrangement,
                  total: this.formAdd.total,
                  quota: this.formAdd.quota,
                  provinceid: this.cityListId,
                  cityid: this.cityListId1,
                  areaid:this.cityListId2,
                  jwt:localStorage.getItem('jwt')
                })
              }).then(res => {
                // console.log(res,'用户增加')
                this.tableShow()
                this.modal3 = false
                this.formAdd = {}
              }).catch(res => {
                this.$Message.error('请求超时,请稍后再试...');
              });
          callback();
        } else{
          this.loading = false;
          this.$nextTick(() => {
            this.loading = true;
          });
          this.$Message.error('新增失败，请填写完整的信息!');
        }
      })
      this.tableShow()
      this.formAdd = {}
    },
    cancel3 () {
      // this.$Message.info('Clicked cancel');
    },
    //用户修改
    xiugai(row){
      // console.log(row)
      this.modal1=true
      this.formItem=row
      this.formItem.Id=row.Id
      this.formItem.cityid=row.cityid
      this.add()
      this.chioce()
      this.chioce1()
      this.chioce2()
    },
    ok (formItem) {
      // console.log(this.$refs[formItem])
      this.modal3=false
      this.$refs[formItem].validate((valid) => {
        if (valid) {
          this.$axios({
            method: 'post',
            url: 'admin/user/upd',
            data: this.$qs.stringify({
              Id:this.formItem.Id,
              name:this.formItem.name,
              phone: this.formItem.phone,
              grade: this.formItem.grade,
              arrangement: this.formItem.arrangement,
              total: this.formItem.total,
              quota: this.formItem.quota,
              pass: this.formItem.pass,
              provinceid: this.cityListId,
              cityid: this.cityListId1,
              areaid:this.cityListId2,
              jwt:localStorage.getItem('jwt')
            })
          }).then(res => {
            // console.log(res,'用户修改')
            this.modal3=false
            this.modal1=false
            this.tableShow()
          }).catch(res => {
            this.$Message.error('请求超时,请稍后再试...');
          });
          callback();
        } else{
          this.loading = false;
          this.$nextTick(() => {
            this.loading = true;
          });
          this.$Message.error('修改失败,请正确填写信息!');
        }
      })
      this.tableShow()
    },
    cancel () {
      this.modal3=false
      // this.$Message.info('Clicked cancel');
    },
    // 修改上传图片
    xiugaiimg(row){
      // console.log(row)
      this.modal2=true
      // this.formImg=row
      this.formImg.Id=row.Id
    },
    handleUpload (file) {
      this.formImg.img = file;
    },
    handleSuccess (response, file, fileList) {
      if(response.code === 200){
        this.modal2=false
        this.tableShow()
      }
    },
    //删除
    delete(row){
      // console.log(row)
      this.$Modal.confirm({
        title: '删除',
        content: '确认删除该条信息吗？',
        onOk: () => {
          this.$axios({
            method: 'post',
            url: 'admin/user/delete',
            data: this.$qs.stringify({
              Id: row.Id,
              jwt:localStorage.getItem('jwt')
            })
          }).then(res => {
            this.tableShow()
          }).catch(res => {
            this.$Message.error('请求超时,请稍后再试...');
          });
        },
        onCancel: () => {
          this.$Message.info('取消');
        }
      });
    },
    //分页
    changepage (index) {
      var _start = (index - 1) * this.pageSize;
      var _end = index * this.pageSize;
      this.data1 = this.ajaxHistoryData.slice(_start, _end);
    }
  }
}
